fromCategory('account')
.when({
   '$init': function() {
        return {
            totalCredits: 0,
            totalDebits: 0
        };
   },
   'AccountCredited': function (s, e) {
       s.totalCredits += 1;
   },
   'AccountDebited': function (s, e) {
       s.totalDebits += 1;
   }
});
